<section class="send_people_section">    <div class="container">      <form method='post' action="<?= LANG_URL.'/vendor/promotePreview/'.$this->uri->segment(3)?>" role="form">          <div class="margin_auto col-md-8">		    <h3>Your total spend is $30 over 6 days</h3>			  <label>Budget</label>      <?php if(isset($post) && $post) { ?>        <input type="hidden" name="send_radio" value="<?=$post['send_radio']?>" />      <?php } ?>				<div class="send_people_range">					<input type="range" name="budget" min="0" max="100">					<output for="foo" onforminput="value = foo.valueAsNumber;"></output>				</div>				<label>Duration (In days)</label>				<div class="send_people_range">					<input type="range" name="duration" min="0" max="100">					<output for="foo" onforminput="value = foo.valueAsNumber;"></output>				</div>								<div class="form-group">					<button type="submit" class="btn btn-primary">Next</button>				</div>		</div>   </form>	</div> 	</section><script>var el, newPoint, newPlace, offset; // Select all range inputs, watch for change$("input[name='budget']").change(function() { // Cache this for efficiency el = $(this);  // Measure width of range input width = el.width();  // Figure out placement percentage between left and right of input newPoint = (el.val() - el.attr("min")) / (el.attr("max") - el.attr("min"));    offset = -1; // Prevent bubble from going beyond left or right (unsupported browsers) if (newPoint < 0) { newPlace = 0; } else if (newPoint > 1) { newPlace = width; } else { newPlace = width * newPoint + offset; offset -= newPoint; }  // Move bubble el   .next("output")   .css({     left: newPlace,     marginLeft: offset + "%"   })     .text(el.val()*4);	  $("input[name='duration']").val(el.val()); $("input[name='duration']")   .next("output")   .css({     left: newPlace,     marginLeft: offset + "%"   })     .text(el.val()); }) // Fake a change to position bubble at page load .trigger('change');  // Select all range inputs, watch for change$("input[name='duration']").change(function() { // Cache this for efficiency el = $(this);  // Measure width of range input width = el.width();  // Figure out placement percentage between left and right of input newPoint = (el.val() - el.attr("min")) / (el.attr("max") - el.attr("min"));    offset = -1; // Prevent bubble from going beyond left or right (unsupported browsers) if (newPoint < 0) { newPlace = 0; } else if (newPoint > 1) { newPlace = width; } else { newPlace = width * newPoint + offset; offset -= newPoint; }   // Move bubble el   .next("output")   .css({     left: newPlace,     marginLeft: offset + "%"   })     .text(el.val());//  alert(el.val());  $("input[name='budget']").val(el.val()); $("input[name='budget']")   .next("output")   .css({     left: newPlace,     marginLeft: offset + "%"   })     .text(el.val()*4); }) // Fake a change to position bubble at page load .trigger('change'); </script>