view</button></a>					<?php } ?>					<?php						if ($this->session->flashdata('error')) {					?>						<div class="alert alert-danger"><?= $this->session->flashdata('error') ?></div>					<?php						}						if ($this->session->flashdata('success')) {					?>						<div class="alert alert-success"><?= $this->session->flashdata('success') ?></div>					<?php						}					?>				</div>                <div class="clearfix"></div>				<div class="col-md-12 row seller_grid">					<div class="col-md-6">						<!--<div class="btn-group">							<button type="button" class="btn btn-primary">All listings</button>							<button type="button" class="btn btn-primary">Auction</button>							<button type="button" class="btn btn-primary">Buy it now</button>						</div> -->   					</div>					<div class="col-md-6 text-right pull-right">						<div class="seller_right_drop">							<div class="form-group pull-left">								<label for="sel1">Sort:</label>								<select>									<option>Best Match</option>									<option>Best Match</option>									<option>Best Match</option>									<option>Best Match</option>								</select>   							</div>						</div>					</div>				</div>				<div class="clearfix"></div>				<div class="col-md-12 seller_products main_products" id="product_section">					<?php					 if (!empty($products)) {					foreach($products as $key) :					$id=base64_encode($key['product_id']);					$productName = escape_product_name($key['product_name']);					?>					<div class="col-md-4 col-sm-4 col-lg-3 col-xl-3 product_ak wow fadeInUp" title="<?=$key['product_name']?>">						<div class="row">							<a href="<?=LANG_URL.'/products-details/'.$productName.'/'.$id?>"><div class="porduct_img"><img src="<?=UPLOAD_URL.'product-images/'.$key['image']?>"/></div></a>							<a href="javascript:void(0);" class="add_to_wishlist" data-product_id="<?=$id?>">							<?php								$blankHeartStyle = '';								$filledHeartStyle = 'style="display:none;"';								if(in_array($key['product_id'],$wishlist_Product_ids))								{									$blankHeartStyle = 'style="display:none;"';									$filledHeartStyle = '';								}							?>								<i class="fa fa-heart-o" <?=$blankHeartStyle?>></i>								<i class="fa fa-heart" <?=$filledHeartStyle?>></i>							</a>							<div class="product_dsp">								<span><?=$key['product_name']?></span>								<a href="<?=LANG_URL.'/products-details/'.$productName.'/'.$id?>"><button class="product_btn">Â£ <?=$key['price']?></button></a>							</div>						</div>					</div>					<?php  endforeach; 						if($productsCount>$productsLimit){					?>						<div class="row load_more_seller_products">							<div class="clearfix"></div>							<input type="button" id="loadMore" value="Load More" class="btm btn-primary"/>						</div>					<?php } } else { ?>						<br>					   <h1 style="color: #ccc;">Products Not Found !</h1>					<?php } ?>				</div>			</div>			</div>		</div> 	    </div> 	</section><script>$('form').on('submit',function(){	if($('#vendor_rating').val()=='')	{		alert('Please select rating first!');	}});var filterSellerProductsURL = '<?=base_url('home/filterSellerProducts/'.$this->uri->segment(2));?>';var offset = 0;var limit = <?=$productsLimit?>;var selected_category = 'all';$('.filter_seller_products').on('click',function(){	var category_id = $(this).data('category_id');	$('#product_section').addClass('ajax_load');	if(category_id!='')	{		offset = 0;		selected_category = category_id;		$.ajax({			url: filterSellerProductsURL,			type: "POST",			data: {'category_id':category_id,'offset':offset},			//dataType: 'json',			success: function (response) {			  if(response != '')			  {				setTimeout(function() {				  $('#product_section').removeClass('ajax_load');				  $('#product_section').html(response);				}, 0);			  }else{				$('#product_section').html('');			  }			},		});	}});$('#product_section').on('click','#loadMore',function(){		offset = limit + offset;	$.ajax({		url: filterSellerProductsURL,		type: "POST",		data: {'category_id':selected_category, 'offset': offset},		//dataType: 'json',		success: function (response) {		  if(response != '')		  {			$('#product_section').find('.load_more_seller_products').remove();			setTimeout(function() {			  $('#product_section').removeClass('ajax_load');			  if(response!='all_products_loaded')			  {				$('#product_section').append(response);			  }			}, 0);		  }else{			$('#product_section').html('');		  }		},	});});var addToWishlistURL = '<?=base_url('home/addToWishlist/');?>';var loginCheck = false;<?php if($this->session->userdata('logged_user') && !empty($this->session->userdata('logged_user'))){ ?>	loginCheck = true;<?php } ?>$('#product_section').on('click','.add_to_wishlist',function(){	var product_id = $(this).data('product_id');	var a_attr = $(this);	if(loginCheck===true)	{		$.ajax({			url: addToWishlistURL,			type: "POST",			data: {'product_id':product_id},			//dataType: 'json',			success: function (response) {			  if(response != '')			  {				if(response == 'already')				{					alert('This product is already added in wishlist!');				}else if(response === 0){					alert('An erro occured.Please try again later!');				}else{					a_attr.find('.fa-heart-o').hide();					a_attr.find('.fa-heart').show();					var wishlistText = $('.cart-wishlist-items').html();					if(wishlistText=='')					{						wishlistText = 1;					}else{						wishlistText = wishlistText.replace('(','');						wishlistText = wishlistText.replace(')','');						wishlistText = parseInt(wishlistText)+1;					}					$('.cart-wishlist-items').html('('+wishlistText+')');				}			  }else{				alert('An erro occured.Please try again later!');			  }			},		});	}else{		window.location.href = "<?=base_url('login')?>";	}});</script>